INSERT INTO `wp_posts` VALUES("17", "1", "2013-09-23 11:51:26", "2013-09-23 11:51:26", "The eight-thousanders are the 14 independent mountains on Earth that are more than 8,000 metres (26,247 ft) high above sea level. All eight-thousanders are located in the Himalayan and Karakoram mountain ranges in Asia.", "Eight Thousanders", "", "inherit", "closed", "closed", "", "6-revision-6", "", "", "2013-09-23 11:51:26", "2013-09-23 11:51:26", "", "6", "http://ec2-54-200-63-145.us-west-2.compute.amazonaws.com/wordpress/?p=17", "0", "revision", "", "0");
