INSERT INTO `wp_posts` VALUES("32", "1", "2013-09-26 12:06:21", "2013-09-26 12:06:21", "<strong>The eight-thousanders are the 14 highest mountains on Earth that are more than 8,000 metres above sea level.</strong>\r\n\r\n<script language=\"JavaScript1.2\">\r\n\r\nvar howOften = 5; //number often in seconds to rotate\r\nvar current = 0; //start the counter at 0\r\nvar ns6 = document.getElementById&&!document.all; //detect netscape 6\r\n\r\n// place your images, text, etc in the array elements here\r\nvar items = new Array();\r\n    items[0]=\"<a href=\'link.htm\' ><img alt=\'image0 (9K)\' src=\"wp-includes/images/an1.jpg\" height=\'600\' width=\'800\' border=\'0\' /></a>\"; //a linked image\r\n    items[1]=\"<a href=\'link.htm\'><img alt=\'image1 (9K)\' src=\"wp-includes/images/an2.jpg\" height=\'600\' width=\'800\' border=\'0\' /></a>\"; //a linked image   \r\nfunction rotater() {\r\n    document.getElementById(\"placeholder\").innerHTML = items[current];\r\n    current = (current==items.length-1) ? 0 : current + 1;\r\n    setTimeout(\"rotater()\",howOften*1000);\r\n}\r\n\r\nfunction rotater() {\r\n    if(document.layers) {\r\n        document.placeholderlayer.document.write(items[current]);\r\n        document.placeholderlayer.document.close();\r\n    }\r\n    if(ns6)document.getElementById(\"placeholderdiv\").innerHTML=items[current]\r\n        if(document.all)\r\n            placeholderdiv.innerHTML=items[current];\r\n\r\n    current = (current==items.length-1) ? 0 : current + 1; //increment or reset\r\n    setTimeout(\"rotater()\",howOften*1000);\r\n}\r\nwindow.onload=rotater;\r\n//-->\r\n</script>\r\n\r\n<style type=\"text/css\"><!--\r\ntable.tftable {font-size:12px;color:#333333;width:100%;border-width: 1px;border-color: #729ea5;border-collapse: collapse;} table.tftable th {font-size:12px;background-color:#acc8cc;border-width: 1px;padding: 8px;border-style: solid;border-color: #729ea5;text-align:left;} table.tftable tr {background-color:#d4e3e5;} table.tftable