INSERT INTO `wp_posts` VALUES("71", "1", "2013-09-27 10:51:09", "2013-09-27 10:51:09", "<script language=\"javascript\" type=\"text/javascript\">\r\nseconds = \"5\";\r\nfunction imgOne()\r\n{\r\ndocument.myimg.src = \'wp-includes/images/ann1.jpg\';	\r\nsetTimeout(\"imgTwo()\", seconds * 1000);\r\n}\r\nfunction imgTwo()\r\n{\r\ndocument.myimg.src = \'wp-includes/images/ann2.jpg\';\r\nsetTimeout(\"imgThree()\", seconds * 1000);\r\n}\r\nfunction imgThree()\r\n{\r\ndocument.myimg.src = \'wp-includes/images/ann3.jpg\';	\r\nsetTimeout(\"imgFour()\", seconds * 1000);\r\n}\r\nfunction imgFour()\r\n{\r\ndocument.myimg.src = \'wp-includes/images/ann4.jpg\';	\r\nsetTimeout(\"imgOne()\", seconds * 1000);\r\n}\r\nwindow.onload=imgOne;\r\n</script>\r\n\r\n<img src=\"wp-includes/images/ann1.jpg\" name=\"myimg\">", "Eight Thousanders", "", "inherit", "closed", "closed", "", "6-revision-27", "", "", "2013-09-27 10:51:09", "2013-09-27 10:51:09", "", "6", "http://ec2-54-200-63-145.us-west-2.compute.amazonaws.com/wordpress/?p=71", "0", "revision", "", "0");
