INSERT INTO `wp_posts` VALUES("83", "1", "2013-09-30 07:02:26", "2013-09-30 07:02:26", "<p><img src=\"wp-includes/images/an_face_south.jpg\" class=\"alignright\" caption=\"North Face\"/>\r\n<BR><BR><BR>This is text that sits above the leaf image that you might be writing about in your post if you were to write about leaves. This is some text that will sit below the text image and you can continue to write about the leaf that is there.</p>\r\n\r\n\r\n<img src=\"wp-includes/images/an_face_north.jpg\" class=\"alignright\"/>\r\n\r\n[insert_php]\r\n$username=\"wordpress\";\r\n$password=\"password\";\r\n$database=\"wordpress\";\r\nmysql_connect(localhost,$username,$password);\r\n@mysql_select_db($database) or die( \"Unable to select database\");\r\n$query=\"SELECT * FROM an_routes ORDER BY first_ascent\";\r\n$result=mysql_query($query);\r\n$num=mysql_numrows($result);\r\nmysql_close();\r\n[/insert_php]\r\n\r\n<style type=\"text/css\">\r\ntable.tftable {font-size:12px;color:#333333;width:90%;margin-left:5%;margin-right:5%;border-width: 1px;border-color: #729ea5;border-collapse: collapse;}\r\ntable.tftable th {font-size:12px;background-color:#acc8cc;border-width: 1px;padding: 8px;border-style: solid;border-color: #729ea5;text-align:left;}\r\ntable.tftable tr {background-color:#d4e3e5;}\r\ntable.tftable td {font-size:12px;border-width: 1px;padding: 8px;border-style: solid;border-color: #729ea5;}\r\n</style>\r\n\r\n<BR CLEAR=\"all\">\r\n<p>\r\nHere is the list of routes and the first ascents of each route.\r\n</p>\r\n\r\n<table id=\"tfhover\" class=\"tftable\" border=\"1\">\r\n<tr>\r\n<th>\r\nRoute Name\r\n</th>\r\n<th>\r\nAscent Date\r\n</th>\r\n<th>\r\nClimber (Last Name)\r\n</th>\r\n<th>\r\nClimber (First Name)\r\n</th>\r\n<th>\r\nClimber (Nationality)\r\n</th>\r\n</tr>\r\n\r\n[insert_php]\r\n$i=0;\r\nwhile ($i < $num) \r\n{$f1=mysql_result($result,$i,\"route_desc\");\r\n$f2=mysql_result($result,$i,\"first_ascent\");\r\n$f3=mysql_result($result,$i,\"climber_last_name\");\r\n$f4=mysql_result($result,$i,\"climber_first_name\");\r\n$f5=mysql_result($result,$i,\"climber_nationality\");\r\necho \'<tr>\r\n<td>\r\n<font face=\"Arial, Helvetica, sans-serif\">\';\r\necho $f1;\r\necho \'</font>\r\n</td>\r\n<td>\r\n<font face=\"Arial, Helvetica, sans-serif\">\';\r\necho $f2;\r\necho \'</td>\r\n<td>\r\n<font face=\"Arial, Helvetica, sans-serif\">\';\r\necho $f3; \r\necho \'</font>\r\n</td>\r\n<td>\r\n<font face=\"Arial, Helvetica, sans-serif\">\';\r\necho $f4; \r\necho \'</font>\r\n</td>\r\n<td>\r\n<font face=\"Arial, Helvetica, sans-serif\">\';\r\necho $f5; \r\necho \'</font>\r\n</td>\r\n</tr>\';\r\n$i++;}[/insert_php]", "Routes", "", "publish", "closed", "closed", "", "first-ascents", "", "", "2013-10-01 13:45:26", "2013-10-01 13:45:26", "", "0", "http://ec2-54-200-63-145.us-west-2.compute.amazonaws.com/wordpress/?page_id=83", "0", "page", "", "0");
