INSERT INTO `wp_posts` VALUES("104", "1", "2013-09-29 14:20:59", "2013-09-29 14:20:59", "[insert_php]\r\n$username=\"wordpress\";\r\n$password=\"password\";\r\n$database=\"wordpress\";\r\nmysql_connect(localhost,$username,$password);\r\n@mysql_select_db($database) or die( \"Unable to select database\");\r\n$query=\"SELECT * FROM an_nations ORDER BY num_ascents DESC\";\r\n$result=mysql_query($query);\r\n$num=mysql_numrows($result);\r\nmysql_close();\r\n[/insert_php]\r\n\r\n<style type=\"text/css\">\r\ntable.tftable {font-size:12px;color:#333333;width:100%;border-width: 1px;border-color: #729ea5;border-collapse: collapse;}\r\ntable.tftable th {font-size:12px;background-color:#acc8cc;border-width: 1px;padding: 8px;border-style: solid;border-color: #729ea5;text-align:left;}\r\ntable.tftable tr {background-color:#d4e3e5;}\r\ntable.tftable td {font-size:12px;border-width: 1px;padding: 8px;border-style: solid;border-color: #729ea5;}\r\n</style>\r\n\r\n<table id=\"tfhover\" class=\"tftable\" border=\"1\">\r\n<tr>\r\n<th>\r\nNation\r\n</th>\r\n<th>\r\nTotal Ascents\r\n</th>\r\n<th>\r\nFirst Ascent\r\n</th>\r\n</tr>\r\n\r\n[insert_php]\r\n$i=0;\r\nwhile ($i < $num) \r\n{$f1=mysql_result($result,$i,\"nation\");\r\n$f2=mysql_result($result,$i,\"num_ascents\");\r\n$f3=mysql_result($result,$i,\"first_ascent\");\r\necho \'<tr>\r\n<td>\r\n<font face=\"Arial, Helvetica, sans-serif\">\';\r\necho $f1;\r\necho \'</font>\r\n</td>\r\n<td>\r\n<font face=\"Arial, Helvetica, sans-serif\">\';\r\necho $f2;\r\necho \'</td>\r\n<td>\r\n<font face=\"Arial, Helvetica, sans-serif\">\';\r\necho $f3; \r\necho \'</font>\r\n</td>\r\n</tr>\';\r\n$i++;}[/insert_php]", "Ascents by Nations", "", "inherit", "closed", "closed", "", "39-revision-32", "", "", "2013-09-29 14:20:59", "2013-09-29 14:20:59", "", "39", "http://ec2-54-200-63-145.us-west-2.compute.amazonaws.com/wordpress/?p=104", "0", "revision", "", "0");
